import './App.css';

import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';


function Account() {
  const accountAPIurl = 'https://api-goerli.etherscan.io/api?module=account&action=balance&address=0x247b669CbDD58FCa982DBf337C5D94880852E3Fa&tag=latest&apikey=PDANBSZU2XWSZAFMQG3EBUX3NRECWBX59E'

  const [data, setData] = useState('');

    useEffect(() => {
      axios.get(accountAPIurl).then((res) => { setData(res.data); console.log(res.data) });
    }, []);

  return(
    <div>{data.result}
      <input type="text" name="Input Account" value=""/>    
    </div>
    // /account로 들어오면 
  )
}



function App() {
  return (

    <BrowserRouter>
    <div className="App">
        <section>
            <Link to="/account">Go for getting account balance in EtherScan</Link>
        </section>

        <section className="features">
            <Routes>
              <Route path="/account" element={<Account />}>Accounts</Route>
            </Routes>
        </section>
    </div>
  </BrowserRouter>
  );
}

export default App;
