import './App.css';

import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';


function Account() {

  const [data, setData] = useState('');
  const [keyword, setKeyword] = useState('');

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const onChange = (event) => setKeyword(event.target.value);

    async function axiosData(keyword) {
      try{
        // 요청이 시작 할 때에는 error를 초기화하고
        setError(null);
        // loading 상태를 true 로 바꿉니다.
        setLoading(true);

        await axios.get(`https://api-goerli.etherscan.io/api?module=account&action=balance&address=${keyword}&tag=latest&apikey=PDANBSZU2XWSZAFMQG3EBUX3NRECWBX59E`)
        .then((res) => { setData(res.data); console.log(res.data); });

      } catch(err) {
        setError(err);
      }
      setLoading(false);
    } 

    //비동기 Side Effect(axios API요청)를 useEffect처리
    useEffect(() => {
      axiosData(keyword);
    }, [keyword]);



    if (loading) return <div>로딩중..</div>; 
    if (error) return <div>에러가 발생했습니다</div>;

    // 아직 users가 받아와 지지 않았을 때는 아무것도 표시되지 않도록 해줍니다.
    if (!data) return null;


  return(
    <div>
      { data.result === "Error! Invalid address format" ? <div>Please input proper account</div> : <div>{data.result/1000000000000000000} ETH</div> }
      <form>
        <input type="text" placeholder="Copy and paste your account.." value={keyword} onChange={onChange}/>
      </form>    
    </div>
  )
}



function App() {
  return (

    <BrowserRouter>
    <div className="App">
        <div class="title">
          Register
        </div>   
        <div class="row visible">
          <input type="text" placeholder="Account" />
          <div class="fa fa-user"></div>
          <div class="nextBtn"> <i class="fa fa-chevron-right"></i> </div>
        </div>


        <div>
            <Link to="/account">Go for getting account balance in EtherScan</Link>
        </div>

        <div className="features">
            <Routes>
              <Route path="/account" element={<Account />}>Accounts</Route>
            </Routes>
        </div>
    </div>
  </BrowserRouter>
  );
}

export default App;
