import './App.css';

import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';


function Account() {
  const accountAPIurl = 'https://api-goerli.etherscan.io/api?module=account&action=balance&address=0x247b669CbDD58FCa982DBf337C5D94880852E3Fa&tag=latest&apikey=PDANBSZU2XWSZAFMQG3EBUX3NRECWBX59E';

  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUsers = async () => {
    try {
      // 요청이 시작 할 때에는 error 와 users 를 초기화하고
      setError(null);
      setData(null);
      // loading 상태를 true 로 바꿉니다.
      setLoading(true);

      const result = await axios.get(accountAPIurl).then((response) => {return response.json()}).then((value) => {return value});
      setData(result);
    } catch(err) {
      setError(err);
    }
    setLoading(false);
  };


  return(
    <ul>{data}</ul>

  )
}



function App() {
  return (

    <BrowserRouter>
    <div className="App">
        <nav>
          <li>
            <Link to="/account">Account</Link>
          </li>
        </nav>


        <section className="features">
            <Routes>
              <Route path="/account" element={<Account />}>Accounts</Route>
            </Routes>
        </section>
    </div>
  </BrowserRouter>
  );
}

export default App;
