import './App.css';

import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import axios from 'axios';


function Account() {

  const [data, setData] = useState('');
  const [keyword, setKeyword] = useState('');

  const onChange = (event) => setKeyword(event.target.value);

    try{
      useEffect((keyword) => {

        axios.get('https://api-goerli.etherscan.io/api?module=account&action=balance&address='+ {keyword} +'&tag=latest&apikey=PDANBSZU2XWSZAFMQG3EBUX3NRECWBX59E')
        .then((res) => { setData(res.data); console.log(res.data) });
      }, [keyword]);
    } catch(err) {
      console.log(err);
    }

  return(
    <div>
      {data.result}
      <form>
        <input type="text" placeholder="Input Account.." value={keyword} onChange={onChange}/>
      </form>    
    </div>
  )
}



function App() {
  return (

    <BrowserRouter>
    <div className="App">
        <section>
            <Link to="/account">Go for getting account balance in EtherScan</Link>
        </section>

        <section className="features">
            <Routes>
              <Route path="/account" element={<Account />}>Accounts</Route>
            </Routes>
        </section>
    </div>
  </BrowserRouter>
  );
}

export default App;
